import logo from './logo.svg';
import './App.css';
import React from 'react';

function App() {
  const [formState,setFormState]=React.useState('login');

  const handleClick=()=>{
    if(formState === 'login') setFormState('register');
    else setFormState('login')
  }

  return (
    <div className="App">
      { formState === 'login' &&
      <div class='inputform'>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h4>Welcome!</h4>
      </header>
      <div>
      <lable for="username" class="formtext">Username &nbsp;</lable>
      <input class="username" type="text" placeholder='Enter the username'></input>
      </div>
      <br></br>
      <div>
      <lable for="password" class="formtext">Password &nbsp;</lable>
      <input class="password" type="password" placeholder='Enter the Password'></input>
      </div>
      <div class='submission'>
      <button type='submit'>Login</button>
      </div>
      <p>Don't have a account <button class='link' onClick={handleClick}>Create account!</button></p>
      </div>
  }
  {
    formState === 'register' && <h1>Register form</h1>
  }
    </div>
  );
}

export default App;
